sys_variable_value.value:
(function execute(inputs, outputs) {
 // ... code ...
 //var item = inputs['reqItem'];
  var item = new GlideRecord('sc_req_item');
   item.get(inputs.reqItem);

 if (inputs['statusCode'] == "200") {
 item.state = 2;
 item.stage = "Waiting for Shipping";
 item.update();
 }
 else {
 item.state = 4;
 item.stage = "Problem with Submission";
 item.update();
 }
})(inputs, outputs);

