sys_variable_value.value:
(function execute(inputs, outputs) {
 try {
 var itemReq = new GlideRecord('sc_req_item');
   itemReq.get(inputs.reqItem);

  var itemName = inputs.catitem;

 var requestedFor = new GlideRecord('sys_user');
   requestedFor.get(itemReq.request.requested_for);

var script = new global.GetDellLCHhardware();
var details = script.getHardwareItem(itemName);

gs.info("model:" + details.Model);
gs.info("DeviceType:" + details.DeviceType);
gs.info("Editon:" + details.Editon);
gs.info("TenantID:" + details.TenantID);
gs.info("Version:" + details.Version);


 var OpenedBy = new GlideRecord('sys_user');
  OpenedBy.get(itemReq.opened_by);

 var requestBody = {};
 var addresses = {};
 var addArray = [];
 var oldItem = {};
 var oldArray = [];
 requestBody.lob = "mgm";
 requestBody.msgID = itemReq.getValue('sys_id');
 requestBody.svcOrderReqNumber = itemReq.getDisplayValue('request');
 requestBody.svcOrderNumber = "";
 requestBody.svcOrderReferenceNumber = itemReq.getValue('number');
 requestBody.svcOrderType = "NEW";
 requestBody.svcOrderPriority = "LOW"; 
 requestBody.svcOrderDeliveryType = "";
 requestBody.specialInstructions = "";
 requestBody.customerOrderIdentifier = itemReq.getValue('number');
 requestBody.customerName = requestedFor.getValue('name');
 requestBody.customerContact = "Cassandra Cupples";// requested by
 requestBody.customerTelephone = requestedFor.getDisplayValue('mobile_phone');
 requestBody.customerEmail = requestedFor.getDisplayValue('email');
 requestBody.customerCompanyBPID = "MGM";
 requestBody.employeeID = requestedFor.getDisplayValue('user_name');
 requestBody.employeeType = "STANDARD";// Requested for Level field 
 requestBody.shipToCompanyName = "";

 addresses.addressType = "Ship To";
 addresses.addressVerified = "true";
 addresses.locationName = "Home";
 addresses.companyName = "MGM";
 addresses.address1 = itemReq.variables.address_line1;// TBD
 addresses.address2 = itemReq.variables.address_line2;// TBD
 addresses.city = itemReq.variables.city_locality;// TBD
 addresses.state = itemReq.variables.state_province;// TBD
 addresses.postalCode = itemReq.variables.postal_code;// TBD
 addresses.countryCode = itemReq.variables.country_code;// TBD
 addArray.push(addresses);// TBD
 requestBody.addresses = addArray;// TBD
 requestBody.organizationalUnit = "";
 requestBody.shippingMethod = "FedEx";
 requestBody.persona = "LAPTOP BASE"; 
 requestBody.profile = "default";
 requestBody.deviceTier = "LATTITUDE 54XX";// from property.
 requestBody.deviceType = "LAPTOP"; //from property
 requestBody.kitProfile = "DEVICE ONLY";// TBD
 requestBody.imageType = "DEFAULT";
 requestBody.productID = "NA";
 requestBody.modeledAfter = "";
 requestBody.hdRetentionIndicator = "False";
 requestBody.cidCoverage = "False";
 requestBody.cidCaseIndicator = "";
 requestBody.requestedDeliveryDate = itemReq.getValue('sys_created_on');//Request ITEM created date timezone TBD
 requestBody.updateDate = "";//will populate if it is update requested  
 requestBody.updateReason = "";//will populate if any
 requestBody.custom = "{ \"memType\": \"AutoPilot\",\"tenantID\":\"ddb8592fed744329bfd2571b7c34699e\",\"domain\":\"MGMRESORTS.COM\",\"memProfile\":\"AutopilotDevices\",\"edition\":\"Windows10\",\"version\":\"21H2\",\"language\":\"US English\"}";//Need to populate via propertytenenet ID addition &version.
 outputs.requestbodyjson = global.JSON.stringify(requestBody);
 gs.info("IOP-Integration" + outputs.requestbodyjson);
 }
 catch (ex) {
 gs.error("IOP-Integration Error: " + ex.message);
 }
})(inputs, outputs);

