sys_transform_entry.source_script:
answer = (function transformEntry(source) {
    if (source.u_itemstatuscode == 'Received') {
        return "Received";
    } else if (source.u_itemstatuscode == 'Open') {
        return "Open";
    } else if (source.u_itemstatuscode == 'Configuration') {
        return "Configuration";
    } else if (source.u_itemstatuscode == 'Ready To Ship') {
        return "Ready To Ship";
    } else if (source.u_itemstatuscode == 'POD') {
        return "POD";
    } else if (source.u_itemstatuscode == 'Cancelled') {
        return "Cancelled";
    } else if (source.u_itemstatuscode == 'ORDER UPDATED') {
        return "ORDER UPDATED";
    } else if (source.u_itemstatuscode == 'Hold Customer') {
        return "Hold Customer";
    }

})(source);
